import { Board } from "./Board";
import { Colors } from "./Colors";
import { Figure } from "./figures/Figure";

export class Cell
{
    readonly x: number;
    readonly y: number;
    readonly color: Colors;
    figure: Figure | null;
    board: Board;
    availible: boolean;
    id: number;
    constructor(board: Board,  x: number, y: number, color: Colors, figure: Figure | null )
    {
        this.x = x;
        this.y = y;
        this.color = color;
        this.board = board;
        this.figure = figure;
        this.availible = false;
        this.id = Math.random();
    }
    moveFigure(target: Cell)
    {
        if(this.figure?.canMove(target)) {this.figure?.moveFigure(target);}
    }

}
